// src/pages/Notes.tsx
import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom'; // Importáljuk a useNavigate-t
// Hozzáadtuk az AppBar, Toolbar importokat
import { TextField, Button, Typography, Container, Box, List, ListItem, ListItemText, IconButton, AppBar, Toolbar } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import { RootState } from '../store/store';
import { fetchNotes, createNote, updateNote, deleteNote } from '../services/notes';
import { logout } from '../store/slices/authSlice'; // Importáljuk a logout action-t
import axios from 'axios'; // *** EZT AZ IMPORTOT MOST HOZZÁADTUK ***

interface Note {
  _id: string;
  title: string;
  content: string;
}

const Notes: React.FC = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [notes, setNotes] = useState<Note[]>([]);
  const [editingNote, setEditingNote] = useState<Note | null>(null);
  const token = useSelector((state: RootState) => state.auth.token);

  const dispatch = useDispatch(); // Használjuk a useDispatch hookot
  const navigate = useNavigate(); // Használjuk a useNavigate hookot

  useEffect(() => {
    loadNotes();
  }, [token]);

  const loadNotes = async () => {
    try {
      const response = await fetchNotes(token!);
      setNotes(response);
    } catch (err) { // Itt az err típusa unknown
      console.error('Failed to load notes:', err);
      // Ha a token érvénytelen vagy lejárt, lehet, hogy ki kell léptetni a felhasználót
      // Ellenőrizzük, hogy axios hiba-e, és van-e válasz 401 státusszal
      if (axios.isAxiosError(err) && err.response && err.response.status === 401) { // *** EZT A SORT MÓDOSÍTOTTUK ***
        dispatch(logout());
        navigate('/login');
      }
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      if (editingNote) {
        await updateNote(editingNote._id, { title, content }, token!);
        setEditingNote(null);
      } else {
        await createNote({ title, content }, token!);
      }
      setTitle('');
      setContent('');
      loadNotes();
    } catch (err) {
      console.error('Failed to save note:', err);
    }
  };

  const handleEdit = (note: Note) => {
    setEditingNote(note);
    setTitle(note.title);
    setContent(note.content);
  };

  const handleDelete = async (id: string) => {
    try {
      await deleteNote(id, token!);
      loadNotes();
    } catch (err) {
      console.error('Failed to delete note:', err);
    }
  };

  // Kilépés kezelő függvény
  const handleLogout = () => {
    dispatch(logout()); // Küldjük a logout action-t a Redux store-nak
    navigate('/login'); // Navigáljunk a bejelentkező oldalra
  };

  return (
    <Box sx={{ flexGrow: 1 }}> {/* AppBar konténer */}
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            My Notes
          </Typography>
          <Button color="inherit" onClick={handleLogout}> {/* Kilépés gomb */}
            Logout
          </Button>
        </Toolbar>
      </AppBar>
      <Container component="main" maxWidth="md" sx={{ mt: 4 }}> {/* Tartalom konténer */}
        <Box component="form" onSubmit={handleSubmit} sx={{ mb: 4 }}>
          <TextField
            fullWidth
            label="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            margin="normal"
            multiline
            rows={4}
          />
          <Button type="submit" variant="contained" sx={{ mt: 2 }}>
            {editingNote ? 'Update Note' : 'Add Note'}
          </Button>
        </Box>
        <List>
          {notes.map((note) => (
            <ListItem
              key={note._id}
              secondaryAction={
                <Box>
                  <IconButton edge="end" onClick={() => handleEdit(note)}>
                    <EditIcon />
                  </IconButton>
                  <IconButton edge="end" onClick={() => handleDelete(note._id)}>
                    <DeleteIcon />
                  </IconButton>
                </Box>
              }
            >
              <ListItemText
                primary={note.title}
                secondary={note.content}
              />
            </ListItem>
          ))}
        </List>
      </Container>
    </Box>
  );
};

export default Notes;
